import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RegisterFuncionario = () => {
    const [funcionarios, setFuncionarios] = useState([]);
    const [nombres, setNombres] = useState('');
    const [apellidos, setApellidos] = useState('');
    const [cedula, setCedula] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');

    useEffect(() => {
        fetchFuncionarios();
    }, []);

    const fetchFuncionarios = async () => {
        const response = await axios.get('/api/funcionarios');
        setFuncionarios(response.data);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('/api/funcionarios', { nombres, apellidos, cedula, fechaNacimiento });
        fetchFuncionarios();
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input value={nombres} onChange={e => setNombres(e.target.value)} placeholder="Nombres" />
                <input value={apellidos} onChange={e => setApellidos(e.target.value)} placeholder="Apellidos" />
                <input value={cedula} onChange={e => setCedula(e.target.value)} placeholder="CÃ©dula" />
                <input value={fechaNacimiento} onChange={e => setFechaNacimiento(e.target.value)} type="date" />
                <button type="submit">Registrar</button>
            </form>
            <ul>
                {funcionarios.map(funcionario => (
                    <li key={funcionario.id}>{funcionario.nombres} {funcionario.apellidos}</li>
                ))}
            </ul>
        </div>
    );
};

export default RegisterFuncionario;
