import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VerFuncionarios = () => {
    const [funcionarios, setFuncionarios] = useState([]);

    useEffect(() => {
        const fetchFuncionarios = async () => {
            try {
                const response = await axios.get('/api/funcionarios');
                setFuncionarios(response.data);
            } catch (error) {
                console.error("Error al obtener los funcionarios:", error);
            }
        };
        fetchFuncionarios();
    }, []);

    return (
        <div>
            <h2>Lista de Funcionarios</h2>
            <ul>
                {funcionarios.map(funcionario => (
                    <li key={funcionario.id}>
                        <strong>Nombres:</strong> {funcionario.nombres}<br />
                        <strong>Apellidos:</strong> {funcionario.apellidos}<br />
                        <strong>CÃ©dula:</strong> {funcionario.cedula}<br />
                        <strong>Fecha de Nacimiento:</strong> {funcionario.fechaNacimiento}<br />
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default VerFuncionarios;
