import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RegisterFuncionario = () => {
    const [funcionarios, setFuncionarios] = useState([]);
    const [nombres, setNombres] = useState('');
    const [apellidos, setApellidos] = useState('');
    const [cedula, setCedula] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        fetchFuncionarios();
    }, []);

    const fetchFuncionarios = async () => {
        try {
            const response = await axios.get('http://localhost:5187/api/funcionarios');
            setFuncionarios(response.data);
        } catch (error) {
            console.error("Error fetching funcionarios:", error);
            setError('Error al obtener los funcionarios.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Reset error message

        try {
            await axios.post('http://localhost:5187/api/funcionarios', { nombres, apellidos, cedula, fechaNacimiento });
            fetchFuncionarios(); // Fetch updated list after successful submission
            setNombres(''); // Clear input fields
            setApellidos('');
            setCedula('');
            setFechaNacimiento('');
        } catch (error) {
            console.error("Error submitting funcionario:", error);
            setError('Error al registrar el funcionario. Verifica la información.');
        }
    };

    return (
        <div>
            <h1>Registrar Funcionario</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    value={nombres}
                    onChange={e => setNombres(e.target.value)}
                    placeholder="Nombres"
                    required
                />
                <input
                    value={apellidos}
                    onChange={e => setApellidos(e.target.value)}
                    placeholder="Apellidos"
                    required
                />
                <input
                    value={cedula}
                    onChange={e => setCedula(e.target.value)}
                    placeholder="Cédula"
                    required
                />
                <input
                    value={fechaNacimiento}
                    onChange={e => setFechaNacimiento(e.target.value)}
                    type="date"
                    required
                />
                <button type="submit">Registrar</button>
            </form>
            <ul>
                {funcionarios.map(funcionario => (
                    <li key={funcionario.id}>{funcionario.nombres} {funcionario.apellidos}</li>
                ))}
            </ul>
        </div>
    );
};

export default RegisterFuncionario;
